import React, { useState } from 'react';
import ChatInterface from './ChatInterface';

const AuthForm = () => {
  const [isRegistering, setIsRegistering] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rePassword, setRePassword] = useState('');
  const [email, setEmail] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (isRegistering) {
      if (password !== rePassword) {
        alert('Passwords do not match!');
        return;
      }
      // Registration logic
      try {
        const response = await fetch('http://localhost:5000/api/register', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ username, password, email })
        });
        const result = await response.json();
        alert(result.message);
        setIsRegistering(false); // Switch back to login after registration
      } catch (error) {
        console.error('Error:', error);
        alert('Registration failed.');
      }
    } else {
      // Login logic
      try {
        const response = await fetch('http://localhost:5000/api/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ username, password })
        });
        const result = await response.json();
        if (result.success) {
          setIsAuthenticated(true); // Set authenticated state to true
        } else {
          alert('Login failed.');
        }
      } catch (error) {
        console.error('Error:', error);
        alert('Login failed.');
      }
    }
  };

  if (isAuthenticated) {
    return <ChatInterface />;
  }

  return (
    <form onSubmit={handleSubmit}>
      <h2>{isRegistering ? 'Register' : 'Login'}</h2>
      <label>
        Username:
        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
      </label>
      <br />
      <label>
        Password:
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
      </label>
      <br />
      {isRegistering && (
        <>
          <label>
            Re-enter Password:
            <input type="password" value={rePassword} onChange={(e) => setRePassword(e.target.value)} required />
          </label>
          <br />
          <label>
            Email:
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </label>
          <br />
        </>
      )}
      <button type="submit">{isRegistering ? 'Register' : 'Login'}</button>
      <br />
      <small>
        {isRegistering ? (
          <span onClick={() => setIsRegistering(false)}>Already have an account? Login</span>
        ) : (
          <span onClick={() => setIsRegistering(true)}>If you don't have an account, please Register</span>
        )}
      </small>
    </form>
  );
};

export default AuthForm;
