import React, { useEffect, useState } from 'react';

const ConversationDetail = ({ conversationId, onBack }) => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/api/messages/${conversationId}`)
      .then(res => res.json())
      .then(data => setMessages(data));
  }, [conversationId]);

  return (
    <div style={{ padding: '20px' }}>
      <button onClick={onBack}>Back to History</button>
      <h3>Conversation {conversationId}</h3>
      <ul>
        {messages.map(msg => (
          <li key={msg.message_id}>
            <b>{msg.sender}:</b> {msg.content} <i>({msg.timestamp})</i>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ConversationDetail;
