import React, { useState } from 'react';
import axios from 'axios';

const TextComponent = ({ text }) => {
  const [wordInfo, setWordInfo] = useState(null);

  const handleWordClick = async (word) => {
    try {
      const response = await axios.get(`/api/vocabulary?word=${word}`);
      setWordInfo(response.data);
    } catch (error) {
      console.error('Error fetching word info:', error);
    }
  };

  const renderText = () => {
    return text.split(' ').map((word, index) => (
      <span key={index} onClick={() => handleWordClick(word)} style={{ cursor: 'pointer', color: 'blue' }}>
        {word}
      </span>
    ));
  };

  return (
    <div>
      <p>{renderText()}</p>
      {wordInfo && (
        <div>
          <h4>Word Information:</h4>
          <p>Meaning: {wordInfo.meaning}</p>
          <p>Pronunciation: {wordInfo.pronunciation}</p>
          <p>Translation: {wordInfo.translation}</p>
        </div>
      )}
    </div>
  );
};

export default TextComponent;
