import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import AuthForm from './components/AuthForm';
import ChatInterface from './components/ChatInterface';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('userId'));
  const userId = localStorage.getItem('userId');

  const handleAuthSuccess = () => {
    setIsAuthenticated(true);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={!isAuthenticated ? <Navigate to="/login" /> : <Navigate to={`/chat/${userId}`} />} />
          <Route path="/login" element={!isAuthenticated ? <AuthForm onAuthSuccess={handleAuthSuccess} /> : <Navigate to={`/chat/${userId}`} />} />
          <Route path="/signup" element={!isAuthenticated ? <AuthForm onAuthSuccess={handleAuthSuccess} isSignup /> : <Navigate to={`/chat/${userId}`} />} />
          <Route path="/chat/:userId" element={isAuthenticated ? <ChatInterface userId={userId} /> : <Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
