import React, { useState, useRef, useEffect } from 'react';
import { FaMicrophone, FaPlay, FaStop } from 'react-icons/fa';
import { GoogleGenAI } from "@google/genai";
import './ChatInterface.css';

const ChatInterface = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isSpeechSupported, setIsSpeechSupported] = useState(false);

  const recognition = useRef(null);
  const synthesis = useRef(window.speechSynthesis);
  const genAI = new GoogleGenAI({ apiKey: "AIzaSyDt-W_YmTQEYB7lyW2wS7c8UyFq-CyKiYE" });

  useEffect(() => {
    if ('SpeechRecognition' in window || 'webkitSpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      recognition.current = new SpeechRecognition();
      recognition.current.continuous = true;
      recognition.current.interimResults = true;
      recognition.current.lang = 'en-US';

      recognition.current.onresult = (event) => {
        const transcript = Array.from(event.results)
          .map(result => result[0])
          .map(result => result.transcript)
          .join('');
        setInputText(transcript);
      };

      recognition.current.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsRecording(false);
      };

      setIsSpeechSupported(true);
    }
  }, []);

  const startRecording = () => {
    if (recognition.current) {
      recognition.current.start();
      setIsRecording(true);
    }
  };

  const stopRecording = () => {
    if (recognition.current) {
      recognition.current.stop();
      setIsRecording(false);
    }
  };

  const handleSend = async () => {
    if (!inputText.trim()) return;

    const newMessage = {
      text: inputText,
      sender: 'user',
      timestamp: new Date().toISOString(),
    };

    setMessages(prev => [...prev, newMessage]);
    setInputText(''); // Clear input after sending

    try {
      const response = await getResponse(inputText);
      const aiMessage = {
        text: response,
        sender: 'ai',
        timestamp: new Date().toISOString(),
      };
      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error getting response:', error);
      // Consider adding a user-friendly error message to the messages state
      setMessages(prev => [...prev, {
        text: "I'm having trouble processing your request. Please try again.",
        sender: 'ai',
        timestamp: new Date().toISOString(),
      }]);

    } finally {
      setIsLoading(false);
    }
  };

  const playText = (text) => {
    if (synthesis.current) {
      setIsPlaying(true);
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = 'en-US';
      utterance.onend = () => setIsPlaying(false);
      synthesis.current.speak(utterance);
    }
  };

  const getResponse = async (text) => {
    try {
      setIsLoading(true);
      const model = genAI.getModel({ model: "gemini-2.0-flash" });

      // Refactor the prompt to include instructions for short answers
      const modifiedPrompt = [
        {
          role: "user",
          parts: [{ text: `Respond to the following in under 100 words: ${text}` }], // Improved prompt
        },
      ];

      const result = await model.generateContent({
        contents: modifiedPrompt,
        generationConfig: {
          temperature: 0.4, // Consider adjusting this value
        },
      });
      const responseText = result.response.candidates[0].content.parts[0].text;
      return responseText;
    } catch (error) {
      console.error('Error:', error);
      return "I apologize, but I'm having trouble. Could you try again?";
    } finally {
      setIsLoading(false);
    }
  };


  return (
    <div className="chat-container">
      {!isSpeechSupported && (
        <div className="warning">Speech recognition is not supported in your browser.</div>
      )}
      <div className="messages-container">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.sender}`}>
            <p>{message.text}</p>
            {message.sender === 'ai' && (
              <button
                onClick={() => playText(message.text)}
                disabled={isPlaying}
                className="play-button"
              >
                {isPlaying ? <FaStop /> : <FaPlay />}
              </button>
            )}
          </div>
        ))}
      </div>

      <div className="input-container">
        <textarea
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Type or speak your message..."
          disabled={isLoading}
        />
        <div className="button-group">
          <button
            className={`record-button ${isRecording ? 'recording' : ''}`}
            onClick={isRecording ? stopRecording : startRecording}
            disabled={!isSpeechSupported || isLoading}
          >
            <FaMicrophone />
          </button>
          <button
            className="send-button"
            onClick={handleSend}
            disabled={!inputText.trim() || isLoading}
          >
            {isLoading ? 'Thinking...' : 'Send'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
