import React, { useState, useRef, useEffect } from 'react';
import { FaMicrophone, FaPlay, FaStop } from 'react-icons/fa';
import './ChatInterface.css';

const ChatInterface = () => {
  const [isLoading, setIsLoading] = useState(false);
  // Remove the client initialization
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isSpeechSupported, setIsSpeechSupported] = useState(false);

  const recognition = useRef(null);
  const synthesis = useRef(window.speechSynthesis);

  useEffect(() => {
    if ('SpeechRecognition' in window || 'webkitSpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      recognition.current = new SpeechRecognition();
      recognition.current.continuous = true;
      recognition.current.interimResults = true;
      recognition.current.lang = 'en-US';

      recognition.current.onresult = (event) => {
        const transcript = Array.from(event.results)
          .map(result => result[0])
          .map(result => result.transcript)
          .join('');
        setInputText(transcript);
      };

      recognition.current.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsRecording(false);
      };

      setIsSpeechSupported(true);
    }
  }, []);

  const startRecording = () => {
    if (recognition.current) {
      recognition.current.start();
      setIsRecording(true);
    }
  };

  const stopRecording = () => {
    if (recognition.current) {
      recognition.current.stop();
      setIsRecording(false);
    }
  };

  const handleSend = async () => {
    if (!inputText.trim()) return;

    const newMessage = {
      text: inputText,
      sender: 'user',
      timestamp: new Date().toISOString(),
    };

    setMessages(prev => [...prev, newMessage]);

    try {
      const response = await getResponse(inputText);
      
      const aiMessage = {
        text: response,
        sender: 'ai',
        timestamp: new Date().toISOString(),
      };

      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error getting response:', error);
    }

    setInputText('');
  };

  const playText = (text) => {
    if (synthesis.current) {
      setIsPlaying(true);
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = 'en-US';
      utterance.onend = () => setIsPlaying(false);
      synthesis.current.speak(utterance);
    }
  };

  const getResponse = async (text) => {
    try {
      setIsLoading(true);
      const messages = [
        { role: "system", content: "You are an English tutor helping students practice English conversation. Provide helpful, encouraging responses and correct any language mistakes." },
        { role: "user", content: text }
      ];

      const response = await fetch(`${process.env.REACT_APP_AZURE_ENDPOINT}/openai/deployments/${process.env.REACT_APP_AZURE_DEPLOYMENT_NAME}/chat/completions?api-version=2023-05-15`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'api-key': process.env.REACT_APP_AZURE_API_KEY
        },
        body: JSON.stringify({
          messages,
          temperature: 0.7,
          max_tokens: 800,
          top_p: 0.95,
        })
      });

      const data = await response.json();
      return data.choices[0].message.content;
    } catch (error) {
      console.error('Error:', error);
      return "I apologize, but I'm having trouble. Could you try again?";
    } finally {
      setIsLoading(false);
    }
  };

  // Update UI to show loading state
  return (
    <div className="chat-container">
      {!isSpeechSupported && (
        <div className="warning">Speech recognition is not supported in your browser.</div>
      )}
      <div className="messages-container">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.sender}`}>
            <p>{message.text}</p>
            {message.sender === 'ai' && (
              <button 
                onClick={() => playText(message.text)}
                disabled={isPlaying}
                className="play-button"
              >
                {isPlaying ? <FaStop /> : <FaPlay />}
              </button>
            )}
          </div>
        ))}
      </div>
      
      <div className="input-container">
        <textarea
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Type or speak your message..."
          disabled={isLoading}
        />
        <div className="button-group">
          <button
            className={`record-button ${isRecording ? 'recording' : ''}`}
            onClick={isRecording ? stopRecording : startRecording}
            disabled={!isSpeechSupported || isLoading}
          >
            <FaMicrophone />
          </button>
          <button 
            className="send-button"
            onClick={handleSend}
            disabled={!inputText.trim() || isLoading}
          >
            {isLoading ? 'Thinking...' : 'Send'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
